import React, {useEffect, useState} from 'react';

import { interval, Subject } from "rxjs";
import { takeUntil } from "rxjs/operators";

export default function StopWatch() {

    const [time, setTime] = useState(0);
    const [watchOn, setWatchOn] = useState(false);

    useEffect(() => {
        const unsubscribe = new Subject();
        interval(10)
            .pipe(takeUntil(unsubscribe))
            .subscribe(() => {
              if (watchOn) {
                setTime(val => val + 1);
              }
            });
        return () => {
          unsubscribe.next();
          unsubscribe.complete();
        };
      }, [watchOn]);

    const handleStart = () => {
        setWatchOn(prevState => !prevState);
    }
    const handleStop = () => {
        setTime(0);
        setWatchOn(false);
      }
    const handleWait = () => {
        if (time !== 0) {
            setWatchOn(false);
        }
    }
    const handleReset = () => {
        setTime(0);
        setWatchOn(prevState => !prevState);
    }

    return (
        <div className='StopWatch'>
            <input type="number" />

            <div className="StopWatch-timer">
                <div>
                    <span>{('0' + Math.floor((time / (1000 * 60 * 60)) % 24)).slice(-2)}h</span>:
                    <span>{('0' + Math.floor(time / 6000)).slice(-2)}m</span>:   
                    <span>{('0' + Math.floor((time / 100) % 60)).slice(-2)}s</span>
                </div>
            </div>

            <div className="button-wrapper">
                <button onClick={handleStart}>Start</button>
                <button onClick={handleStop}>Stop</button>
                <button onClick={handleWait}>Wait</button>
                <button onClick={handleReset}>Reset</button>
            </div>
        </div>
    )
}
